import React from "react";
import { Card, CardHeader, CardContent, CardMedia, CardActions, Typography } from '@mui/material';

const Connected = (props) => {
  return (
    <div className="connected-container">
      <h1 className="connected-header">You are Connected to Metamask</h1>
      <p className="connected-account">Metamask Account: {props.account}</p>
      <p className="connected-account">Remaining Time: {props.remainingTime}</p>
      {props.showButton ? (
        <p className="connected-account">You have already voted</p>
      ) : (
        <div>
          <input
            type="number"
            placeholder="Enter Candidate Index"
            value={props.number}
            onChange={props.handleNumberChange}
          />
          <br />
          <button className="login-button" onClick={props.voteFunction}>
            Vote
          </button>
        </div>
      )}

      <div className="cards-container" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {props.candidates.map((candidate, index) => (
          <div key={index} style={{ width: '25%', padding: '10px' }}>
            <Card sx={{ maxWidth: 345, minWidth: 300 }}>
              <CardHeader
                title={
                  <Typography align="center" variant="subtitle1">
                    {candidate.name}
                  </Typography>
                }
              />
              <CardContent>
                <CardMedia
                  component="img"
                  alt={`Candidate ${index + 1}`}
                  height="140"
                  image={candidate.image}
                />
              </CardContent>
              <CardActions sx={{ justifyContent: "center" }}>
                <Typography align="center" variant="body1">
                  <strong>{candidate.voteCount}</strong> votes
                </Typography>
              </CardActions>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Connected;
